import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "./paymentform.css";
import { BASE_URL } from "../utils/config";

const PaymentForm = () => {
  const { bookingid } = useParams();
  const totalamountParam = new URLSearchParams(window.location.search).get(
    "totalamount"
  );
  const totalamount = totalamountParam ? parseInt(totalamountParam) : 0;

  const [formData, setFormData] = useState({
    bookingId: bookingid,
    name: "",
    cardnumber: "",
    expiry: "",
    cvv: "",
    amount: totalamount,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handlePay = async () => {
    console.log(formData);

    try {
      const response = await fetch(`${BASE_URL}/pay/`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (response.ok) {
        console.log("Payment created successfully:", data);
        // Handle success response
      } else {
        console.error("Failed to create payment:", data.message);
        // Handle error response
      }
    } catch (err) {
      console.error("Error creating payment:", err);
      // Handle fetch error
    }
  };

  return (
    <div className="payment-form">
      <h2>Payment Details</h2>
      <label htmlFor="nameOnCard">Name on Card:</label>
      <input
        type="text"
        id="nameOnCard"
        name="nameOnCard"
        value={formData.name || ""}
        onChange={handleInputChange}
        required
      />
      <label htmlFor="cardNumber">Card Number:</label>
      <input
        type="text"
        id="cardNumber"
        name="cardNumber"
        value={formData.cardnumber}
        onChange={handleInputChange}
        required
      />
      <label htmlFor="expiryDate">Expiry Date:</label>
      <input
        type="date"
        id="expiryDate"
        name="expiryDate"
        value={formData.expiry}
        onChange={handleInputChange}
        required
      />
      <label htmlFor="cvv">CVV:</label>
      <input
        type="text"
        id="cvv"
        name="cvv"
        value={formData.cvv}
        onChange={handleInputChange}
        required
      />
      <button onClick={handlePay}>Pay {totalamount}</button>
    </div>
  );
};

export default PaymentForm;
