import React, { useRef } from "react";
import "./search-bar.css";
import { Col, Form, FormGroup } from "reactstrap";
import { BASE_URL } from "../utils/config";
import { useNavigate } from "react-router-dom";

const SearchBar = () => {
  const locationRef = useRef("");
  const distanceRef = useRef(0);
  const maxGroupSizeRef = useRef(0);
  const navigate = useNavigate();

  const searchHandler = async () => {
    const location = locationRef.current.value;
    const distance = distanceRef.current.value;
    const maxGroupSize = maxGroupSizeRef.current.value;

    if (location == "" || distance == "" || maxGroupSize == "") {
      return alert("All fields are required");
    }

    const res = await fetch(
      `${BASE_URL}/cars/search/getCarBySearch?city=${location}&distance=${distance}&maxGroupSize=${maxGroupSize}`
    );
    if (!res.ok) alert("Something went wrong");

    const result = await res.json();

    navigate(
      `/car/search?city=${location}&distance=${distance}&maxGroupSize=${maxGroupSize}`,
      { state: result.data }
    );
  };

  return (
    <Col lg="12" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      <div className="search_bar">
        <Form className="d-flex align-items-center gap-4">
        <FormGroup className="d-flex gap-3 form__group form__group-fast">
            <span>
            <i class="ri-car-fill"></i>
            </span>
            <div>
              <h6>Find your car</h6>
              <input
                type="text"
                placeholder="Car name"
                ref={locationRef}
              />
            </div>
          </FormGroup>
          <FormGroup className="d-flex gap-3 form__group form__group-fast">
            <span>
              <i class="ri-map-pin-time-line"></i>
            </span>
            <div>
              <h6>Transmission</h6>
              <input
                type="text"
                placeholder="AT/MN"
                ref={distanceRef}
              />
            </div>
          </FormGroup>
          <FormGroup className="d-flex gap-3 form__group form__group-last">
            <span>
              <i class="ri-water-flash-line"></i>
            </span>
            <div>
              <h6>Fuel Type</h6>
              <input type="Text" 
              placeholder="Petrol/Diesel" 
              ref={maxGroupSizeRef} />
            </div>
          </FormGroup>
          <FormGroup className="d-flex gap-3 form__group form__group-fast">
            <span>
              <i class="ri-map-pin-line"></i>
            </span>
            <div>
              <h6>Location</h6>
              <input
                type="text"
                placeholder="Find nearby dealers"
                ref={locationRef}
              />
            </div>
          </FormGroup>
          <span className="search__icon" type="submit" onClick={searchHandler}>
            <i class="ri-search-line"></i>
          </span>
        </Form>
      </div>
    </Col>
  );
};

export default SearchBar;
