import React, { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import { BASE_URL } from "../utils/config";
import { AuthContext } from "../context/AuthContext";
import "./userbookings.css";

const UserBookings = () => {
  const { user } = useContext(AuthContext);
  const id = user._id;

  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [showPaymentModal, setShowPaymentModal] = useState(false);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await fetch(`${BASE_URL}/booking/${id}`, {
          headers: {
            "content-type": "application/json",
          },
          method: "GET",
          credentials: "include",
        });

        if (!response.ok) {
          throw new Error(`Failed to fetch data: ${response.status}`);
        }

        const data = await response.json();
        setBookings(data.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchBookings();
  }, [user, id]);

  const handlePay = (bookingId) => {
    const selected = bookings.find((booking) => booking._id === bookingId);
    setSelectedBooking(selected);
    setShowPaymentModal(true);
  };

  const handleCloseModal = () => {
    setShowPaymentModal(false);
  };

  const handlePaymentSubmit = (e) => {
    e.preventDefault();
    // Handle payment submission here
    console.log("Payment submitted");
    // Close the modal after payment submission
    setShowPaymentModal(false);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      <h2>User Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings found for this user.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Tour Name</th>
              <th>Full Name</th>
              <th>Guest Size</th>
              <th>Phone</th>
              <th>Booked At</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking) => (
              <tr key={booking._id}>
                <td>{booking.tourName}</td>
                <td>{booking.fullName}</td>
                <td>{booking.guestSize}</td>
                <td>{booking.phone}</td>
                <td>{new Date(booking.bookAt).toLocaleString()}</td>
                <td>
                  {booking.status === "Requested" && (
                    <button disabled={true} className="requested-button">
                      Requested
                    </button>
                  )}
                  {booking.status === "accepted" && (
                    <button
                      onClick={() => handlePay(booking._id)}
                      className="pay-button"
                    >
                      Pay
                    </button>
                  )}
                  {booking.status === "paid" && (
                    <button disabled={true} className="paid-button">
                      Paid
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {showPaymentModal && selectedBooking && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>
              &times;
            </span>
            <h2>Payment Details</h2>
            <p>Amount to be paid: {selectedBooking.amount}</p>
            <form onSubmit={handlePaymentSubmit}>
              <label>
                Card Number:
                <input type="text" name="cardNumber" />
              </label>
              <label>
                CVV:
                <input type="text" name="cvv" />
              </label>
              <label>
                Expiry Date:
                <input type="text" name="expiryDate" />
              </label>
              <label>
                Name on Card:
                <input type="text" name="nameOnCard" />
              </label>
              <input type="submit" value="Pay" />
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserBookings;
