import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import { BASE_URL } from "../utils/config";
import { AuthContext } from "../context/AuthContext";
import "./userbookings.css";

const UserBookings = () => {
  const { user } = useContext(AuthContext);
  const id = user._id;

  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await fetch(`${BASE_URL}/booking/${id}`, {
          headers: {
            "content-type": "application/json",
          },
          method: "GET",
          credentials: "include",
        });

        if (!response.ok) {
          throw new Error(`Failed to fetch data: ${response.status}`);
        }

        const data = await response.json();
        setBookings(data.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchBookings();
  }, [user, id]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }
  let bookingNumber = 1;

  return (
    <div>
      <h2>User Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings found for this user.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Booking Number</th>
              <th>Car Name</th>
              <th>Full Name</th>
              {/* <th>Guest Size</th> */}
              <th>Phone</th>
              <th>Booked At</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking) => (
              <tr key={booking._id}>
                <td>{bookingNumber++}</td>
                <td>{booking.carName}</td>
                <td>{booking.fullName}</td>
                {/* <td>{booking.guestSize}</td> */}
                <td>{booking.phone}</td>
                <td>{new Date(booking.bookAt).toLocaleString()}</td>
                <td>
                  {booking.status === "requested" && (
                    <button disabled={true} className="requested-button">
                      Requested
                    </button>
                  )}
                  {booking.status === "accepted" && (
                    <Link
                      to={`/payment/${booking._id}?totalamount=${
                        booking.totalamount || 0
                      }`}
                      className="pay-button"
                    >
                      Pay
                    </Link>
                  )}
                  {booking.status === "paid" && (
                    <button disabled={true} className="paid-button">
                      Paid
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default UserBookings;
