import React, { useState, useEffect } from "react";
import { BASE_URL } from "../utils/config";
import "./ToursAdmin.css";
import { Button, Form, FormGroup, Label, Input } from "reactstrap";

const Toursadmin = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddTourForm, setShowAddTourForm] = useState(false);
  const [newTourData, setNewTourData] = useState({
    title: "",
    city: "",
    address: "",
    distance: 0,
    photo: "",
    desc: "",
    price: 0,
    maxGroupSize: 0,
    featured: false,
  });

  const [editMode, setEditMode] = useState(false);
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  const [editTourData, setEditTourData] = useState({
    id: null,
    title: "",
    city: "",
    address: "",
    distance: 0,
    photo: "",
    desc: "",
    price: 0,
    maxGroupSize: 0,
    featured: false,
  });

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const response = await fetch(`${BASE_URL}/tours?page=${page}`, {
          method: "GET",
          credentials: "include",
        });
        if (!response.ok) {
          throw new Error("Failed to fetch tours");
        }
        const data = await response.json();
        setTours(data.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching tours:", error);
        setLoading(false);
      }
    };

    const fetchTourCount = async () => {
      try {
        const response = await fetch(`${BASE_URL}/tours/search/getTourCount`, {
          method: "GET",
          credentials: "include",
        });
        if (!response.ok) {
          throw new Error("Failed to fetch tour count");
        }
        const { data: tourCount } = await response.json();
        const pages = Math.ceil(tourCount / 8);
        setPageCount(pages);
      } catch (error) {
        console.error("Error fetching tour count:", error);
      }
    };

    fetchTours();
    fetchTourCount();
  }, [page]);

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`${BASE_URL}/tours/${id}`, {
        method: "DELETE",
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to delete tour");
      }

      setTours((prevTours) => prevTours.filter((tour) => tour._id !== id));
    } catch (error) {
      console.error("Error deleting tour:", error);
    }
  };

  const handleAddTourClick = () => {
    setShowAddTourForm(true);
  };

  const handleEditClick = (tour) => {
    setEditMode(true);
    setEditTourData({
      id: tour._id,
      title: tour.title,
      city: tour.city,
      address: tour.address,
      distance: tour.distance,
      photo: tour.photo,
      desc: tour.desc,
      price: tour.price,
      maxGroupSize: tour.maxGroupSize,
      featured: tour.featured,
    });
    setShowAddTourForm(true);
  };

  const handleAddTourChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (editMode) {
      setEditTourData((prevData) => ({
        ...prevData,
        [name]: type === "checkbox" ? checked : value,
      }));
    } else {
      setNewTourData((prevData) => ({
        ...prevData,
        [name]: type === "checkbox" ? checked : value,
      }));
    }
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    try {
      const endpoint = editMode
        ? `${BASE_URL}/tours/update/${editTourData.id}`
        : `${BASE_URL}/tours/create`;
      const method = editMode ? "PUT" : "POST";

      const response = await fetch(endpoint, {
        method: method,
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(editMode ? editTourData : newTourData),
      });

      if (!response.ok) {
        throw new Error(
          editMode ? "Failed to update tour" : "Failed to add tour"
        );
      }

      const result = await response.json();
      const updatedTour = result.data;

      if (editMode) {
        setTours((prevTours) =>
          prevTours.map((tour) =>
            tour._id === updatedTour._id ? updatedTour : tour
          )
        );
      } else {
        setTours((prevTours) => [...prevTours, updatedTour]);
      }

      setEditMode(false);
      setShowAddTourForm(false);
      setEditTourData({
        id: null,
        title: "",
        city: "",
        address: "",
        distance: 0,
        photo: "",
        desc: "",
        price: 0,
        maxGroupSize: 0,
        featured: false,
      });
      setNewTourData({
        title: "",
        city: "",
        address: "",
        distance: 0,
        photo: "",
        desc: "",
        price: 0,
        maxGroupSize: 0,
        featured: false,
      });
    } catch (error) {
      console.error(`Error ${editMode ? "updating" : "adding"} tour:`, error);
    }
  };

  return (
    <div>
      <h1>Tours</h1>
      <Button variant="contained" color="primary" onClick={handleAddTourClick}>
        Add tour
      </Button>
      {showAddTourForm && (
        <Form className="form-container" onSubmit={handleFormSubmit}>
          <FormGroup>
            <Label for="title">Title</Label>
            <Input
              type="text"
              name="title"
              id="title"
              placeholder="Enter title"
              value={editMode ? editTourData.title : newTourData.title}
              onChange={handleAddTourChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="city">City</Label>
            <Input
              type="text"
              name="city"
              id="city"
              placeholder="Enter city"
              value={editMode ? editTourData.city : newTourData.city}
              onChange={handleAddTourChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="address">Address</Label>
            <Input
              type="text"
              name="address"
              id="address"
              placeholder="Enter address"
              value={editMode ? editTourData.address : newTourData.address}
              onChange={handleAddTourChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="distance">Distance</Label>
            <Input
              type="number"
              name="distance"
              id="distance"
              placeholder="Enter distance"
              value={editMode ? editTourData.distance : newTourData.distance}
              onChange={handleAddTourChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="photo">Photo URL</Label>
            <Input
              type="text"
              name="photo"
              id="photo"
              placeholder="Enter photo URL"
              value={editMode ? editTourData.photo : newTourData.photo}
              onChange={handleAddTourChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="desc">Description</Label>
            <Input
              type="textarea"
              name="desc"
              id="desc"
              placeholder="Enter description"
              value={editMode ? editTourData.desc : newTourData.desc}
              onChange={handleAddTourChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="price">Price</Label>
            <Input
              type="number"
              name="price"
              id="price"
              placeholder="Enter price"
              value={editMode ? editTourData.price : newTourData.price}
              onChange={handleAddTourChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="maxGroupSize">Max Group Size</Label>
            <Input
              type="number"
              name="maxGroupSize"
              id="maxGroupSize"
              placeholder="Enter max group size"
              value={
                editMode ? editTourData.maxGroupSize : newTourData.maxGroupSize
              }
              onChange={handleAddTourChange}
              required
            />
          </FormGroup>
          {/* Featured field (assuming it's a boolean) */}
          <FormGroup check>
            <Label check>
              <Input
                type="checkbox"
                id="featured"
                name="featured"
                checked={
                  editMode ? editTourData.featured : newTourData.featured
                }
                onChange={handleAddTourChange}
              />
              Featured
            </Label>
          </FormGroup>
          <Button color="primary" type="submit">
            {editMode ? "Update" : "Save"}
          </Button>{" "}
          <Button color="secondary" onClick={() => setShowAddTourForm(false)}>
            Cancel
          </Button>
        </Form>
      )}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <div className="tour-card-container">
            {tours.map((tour) => (
              <div key={tour._id} className="tour-card">
                <img src={tour.photo} alt="tour-img" />
                <h2>{tour.title}</h2>
                <h4>Rs.{tour.price}</h4>
                <p>{tour.address}</p>
                <p>{tour.desc}</p>

                <div className="button-container">
                  <Button
                    color="danger"
                    variant="contained"
                    onClick={() => handleDelete(tour._id)}
                  >
                    Delete
                  </Button>{" "}
                  <span className="spacer"></span>{" "}
                  <Button
                    color="primary"
                    variant="contained"
                    onClick={() => handleEditClick(tour)}
                  >
                    Edit
                  </Button>
                </div>
              </div>
            ))}
          </div>
          <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
            {[...Array(pageCount).keys()].map((number) => (
              <span
                key={number}
                onClick={() => setPage(number)}
                className={page === number ? "active__page" : ""}
              >
                {number + 1}
              </span>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Toursadmin;
