import React, { useState, useContext, useEffect } from "react";
import { Col, Container, Form, FormGroup, Row } from "reactstrap";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { BASE_URL } from "../utils/config";

const Login = () => {
  const [credentials, setCredentials] = useState({
    email: "",
    password: "",
  });

  const { dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleLogin = async () => {
    dispatch({ type: "LOGIN_START" });

    try {
      const res = await fetch(`${BASE_URL}/auth/login`, {
        method: "post",
        headers: { "content-type": "application/json" },
        credentials: "include",
        body: JSON.stringify(credentials),
      });

      const result = await res.json();

      if (!res.ok) {
        alert(result.message);
        dispatch({ type: "LOGIN_FAILURE", payload: result.message });
        return;
      }

      dispatch({ type: "LOGIN_SUCCESS", payload: result.data });

      const fetchedUserRole = result.role;

      // Store the user role in sessionStorage
      sessionStorage.setItem("userRole", fetchedUserRole);

      if (fetchedUserRole === "user") {
        navigate("/home");
      } else if (fetchedUserRole === "admin") {
        navigate("/adminHome");
      } else {
        navigate("/");
      }
    } catch (err) {
      dispatch({ type: "LOGIN_FAILURE", payload: err.message });
    }
  };

  const handleBrowserBack = () => {
    // Retrieve the previous path from sessionStorage
    const previousPath = sessionStorage.getItem("previousPath");
    if (previousPath) {
      navigate(previousPath);
    } else {
      navigate("/");
    }
  };

  // useEffect(() => {
  //   handleBrowserBack();
  // }, []);

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8" className="m-auto">
            <div className="login__container d-flex justify-content-between">
              <div className="login__form">
                <h2>Login</h2>

                <Form
                  onSubmit={(e) => {
                    e.preventDefault();
                    handleLogin();
                  }}
                >
                  <FormGroup>
                    <input
                      type="email"
                      placeholder="Email"
                      required
                      id="email"
                      onChange={handleChange}
                    />
                  </FormGroup>
                  <FormGroup>
                    <input
                      type="password"
                      placeholder="Password"
                      required
                      id="password"
                      onChange={handleChange}
                    />
                  </FormGroup>
                  <button
                    className="btn secondary__btn auth__btn"
                    type="submit"
                  >
                    Login
                  </button>
                </Form>
                <p>
                  Don't have an account? <Link to="/register">Create</Link>
                </p>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Login;
